mapper.select:
select id,starting_time, ending_time, question_id_list, course_id, teacher_id
from exam
where id = #{id,jdbcType=INTEGER}

select id,starting_time, ending_time, question_id_list, course_id, teacher_id
from exam
where course_id = #{courseId,jdbcType=INTEGER}

select id,starting_time, ending_time, question_id_list, course_id, teacher_id
from exam
where teacher_id = #{teacherId,jdbcType=INTEGER}
